class BankAccount:
    def __init__(self):
        self.balance = 0

    def deposit(self, amount):
        if amount > 0:
            self.balance += amount
        else:
            print("Invalid deposit amount.")

    def withdraw(self, amount):
        if self.balance >= amount and amount > 0:
            self.balance -= amount
        else:
            print("Insufficient funds or invalid withdrawal amount.")

    def check_balance(self):
        return self.balance

# Example usage:

account = BankAccount()

account.deposit(100)
print(account.check_balance())  # Output: 100

account.withdraw(50)
print(account.check_balance())  # Output: 50

account.withdraw(70)  # Output: Insufficient funds or invalid withdrawal amount.
print(account.check_balance())  # Output: 50 (remains the same as previous balance)

account.deposit(-20)  # Output: Invalid deposit amount.

def bubble_sort(lst):
    n = len(lst)
    for i in range(n-1):
        for j in range(0, n-i-1):
            if lst[j] > lst[j+1]:
                lst[j], lst[j+1] = lst[j+1], lst[j]
    return lst

# Example usage
my_list = [5, 3, 8, 4, 2]
sorted_list = bubble_sort(my_list)
print(sorted_list)  # Output: [2, 3, 4, 5, 8]

