    def max_path_sum(self, grid):
        m = len(grid)
        n = len(grid[0])
        dirs = [[-1, 0], [0, -1], [1, 0], [0, 1]]  # directions
        def dfs(grid, i, j, total):
            if i == m - 1 and j == n - 1:
                return total
            max_total = 0
            for di, dj in dirs:
                new_i, new_j = i + di, j + dj
                if 0 <= new_i < m and 0 <= new_j < n:
                    max_total = max(max_total, dfs(grid, new_i, new_j, total + grid[new_i][new_j]))
            return max_total

        return dfs(grid, 0, 0, 0)
