class Solution:
    def findPath(self, grid: List[List[int]]) -> List[int]:
        m, n = len(grid), len(grid[0])
        queue = deque()

        # check if a (i, j) exists in the board 
        def is_valid(i, j): 
            return 0 <= i < m and 0 <= j < n

        # bfs
        for i in range(m):
            for j in range(n):
                if grid[i][j] == 1:
                    queue.append((i,j))

        while queue:    
            x, y = queue.popleft()

            # check next (x+1, y), (x-1, y), (x, y+1), and (x, y-1)
            for dx, dy in [[0, 1], [1, 0], [-1, 0], [0, -1]]:
                nx = x + dx
                ny = y + dy

                # check if next (x, y) is valid and has a value on the grid
                if not is_valid(nx, ny) or not grid[nx][ny]:
                    continue

                queue.append((nx, ny))

                # update max sum in grid 
                if grid[nx][ny] + grid[x][y] > grid[nx-1][ny] and grid[nx+1][ny] and grid[nx][ny+1] or grid[nx][ny-1]:
                    grid[nx-1][ny] = 0
                    grid[nx][ny] = grid[nx][ny] + grid[x][y]
                    if nx == m-1:
                        return [grid[m-2][n-1], grid[m-2][n], grid[m-1][n]]
