const App = () => {
  const [state, setState] = React.useState({ counter: 0 });

  function handleCounterChange(event) {
    event.preventDefault(); // Prevent form submission
    setState((prevState) => ({ ...prevState, counter: prevState.counter + 1 }));
  }

  return (
    <div>
      <form onSubmit={handleCounterChange}>
        <label for="counter">Increment by:</label>
        <input type="number" id="counter" name="counter" value={state.counter} onChange={e => setState((prevState) => ({ ...prevState, counter: e.target.value }))} />
        <button type="submit">Submit</button>
      </form>
      <Counter />
    </div>
  );
};
