#include <iostream>
#include <vector>
#define MAX_N 1000
using namespace std;

int n, m;
int grid[MAX_N][MAX_N];
bool visited[MAX_N][MAX_N];
int di[2] = {-1, -1};
int dj[2] = {-1, -1};
int dist[MAX_N][MAX_N];
int max_dist = 0;
int ans = 0;
vector<pair<int, int>> path;

void dfs(int i, int j) {
    if (i == n && j == m) {
        if (ans < grid[i][j]) {
            ans = grid[i][j];
            path.clear();
            while (i != -1 && j != -1) {
                path.push_back((i, j));
                i = di[0];
                j = dj[0];
            }
            while (!path.empty()) {
                cout << path.back().first << " " << path.back().second << endl;
                path.pop_back();
            }
        }
        return;
    }

    if (i == -1 || j == -1) {
        if (!visited[i][j]) {
            visited[i][j] = 1;
            dfs(i + di[0], j + dj[0]);
            visited[i][j] = 0;
        }
        return;
    }

    if (dist[i][j] >= max_dist)
        return;

    dist[i][j] = grid[i][j];
    dfs(i + di[0], j + dj[0]);
}

int main() {
    cin >> n >> m;
    for (int i = 0; i < n; ++i) {
        for (int j = 0; j < m; ++j) {
            cin >> grid[i][j];
        }
    }
    memset(dist, 0, sizeof(dist));
    dfs(0, 0);
    cout << ans << endl;
    return 0;
}
