class BankAccount:
    def __init__(self, initial_balance=0):
        self.balance = initial_balance

    def deposit(self, amount):
        if amount > 0:
            self.balance += amount
            return True
        else:
            print("Invalid deposit amount.")
            return False

    def withdraw(self, amount):
        if amount <= self.balance:
            self.balance -= amount
            return True
        else:
            print("Insufficient funds for withdrawal.")
            return False

    def check_balance(self):
        return self.balance

# Example usage:
account = BankAccount(100)
print(account.check_balance())  # Output: 100
account.deposit(50)
print(account.check_balance())  # Output: 150
account.withdraw(25)
print(account.check_balance())  # Output: 125

def bubble_sort(arr):
    n = len(arr)
    for i in range(n):
        for j in range(0, n - i - 1):
            if arr[j] > arr[j + 1]:
                arr[j], arr[j + 1] = arr[j + 1], arr[j]
    return arr

# Example usage:
print(bubble_sort([5, 3, 2, 8, 9]))  # Output: [2, 3, 5, 8, 9

def fibonacci(n):
    if n <= 0:
        return "Invalid input. Please enter a positive integer."
    elif n == 1:
        return 0
    elif n == 2:
        return 1
    else:
        return fibonacci(n-1) + fibonacci(n-2)

